svg.script:
var startZoom = 1;
var maxZoom = 1e6;
var maxSpiralCount = 10;
var initialPause = 5000;
var spiralTimeIncrement = 500;
var timeIncrement = 10;

var zoom = startZoom;
var zoomFactor = 1.01;
var container = document.getElementById("container");
var count = 0;
var spiralCount = 0;

function getPoints(steps) {
  var points = [];
  var horiz = true;
  var x = 1;
  var y = 0;
  points.push({x: x, y: y});
  var dist = 1;
  x += dist;
  points.push({x: x, y: y});
  for (var i = 1; i < steps; i++) {
    horiz = !horiz;
    dist = dist * Math.PI / i;
    if (i % 2 == 1) {
      dist = -dist;
    }
    if (horiz) {
      x += dist;
    } else {
      y += dist;
    }
    points.push({x: x, y: y});
  }
  return points;
}

function getPointsAsPath(points, scale) {
  var d = "";
  points.forEach(function(p, index, array) {
    d += (d.length == 0 ? "M" : "L") + " " + (scale * p.x) + " " + (scale * p.y) + " ";
  });
  return d;
}

var points = getPoints(20);

function spiral() {
  spiralCount++;
  if (spiralCount == maxSpiralCount) {
    spiralCount = points.length;
  }
  var d = getPointsAsPath(points.slice(0, spiralCount), 1);
  document.getElementById("path").setAttribute('d', d);
  if (spiralCount == points.length) {
    return;
  }
  setTimeout(spiral, spiralTimeIncrement);
}

function zoomIn() {
  zoom *= zoomFactor;
  if (zoom >= maxZoom) {
    start();
    return;
  }
  var d = getPointsAsPath(points, zoom);
  document.getElementById("path").setAttribute('d', d);
  setTimeout(zoomIn, timeIncrement);
}

function start() {
  zoom = startZoom;
  spiralCount = 0;
  setTimeout(spiral, spiralTimeIncrement);
  setTimeout(zoomIn, initialPause);
}

start();
