svg.script:
var startZoom = 1;
var initialPause = 2000;
var timeIncrement = 10;

var zoom = startZoom;
var container = document.getElementById("container");
var count = 0;

function getPath(steps) {
  var d = "M 1 0 h 1 ";
  var horiz = true;
  var dist = 1;
  for (var i = 1; i < steps; i++) {
    horiz = !horiz;
    dist = dist * Math.PI / i;
    if (i % 2 == 1) {
      dist = -dist;
    }
    d += (horiz ? "h" : "v") + " " + dist + " ";
  }
  console.log(d);
  return d;
}

var path = document.getElementById("path");
path.setAttribute('d', getPath(20));

function repeat() {
  timeout = timeIncrement;
  zoom *= 1.01;
  if (zoom > 1000000) {
    zoom = startZoom;
    timeout = initialPause;
  }
  if (count++ % 100 == 0) {
    console.log(zoom);
  }
  container.setAttribute('transform','scale(' + zoom + ')');
  container.setAttribute('stroke-width', 0.025 * startZoom /zoom); // constant width
  setTimeout(repeat, timeout);
}
setTimeout(repeat, initialPause);
