svg.script:
var startZoom = 1;
var initialPause = 5000;
var timeIncrement = 10;

var zoom = startZoom;
var container = document.getElementById("container");
var count = 0;

function getPoints(steps) {
  var points = [];
  var horiz = true;
  var x = 1;
  var y = 0;
  var dist = 1;
  x += dist;
  points.push({x: x, y: y});
  for (var i = 1; i < steps; i++) {
    horiz = !horiz;
    dist = dist * Math.PI / i;
    if (i % 2 == 1) {
      dist = -dist;
    }
    if (horiz) {
      x += dist;
    } else {
      y += dist;
    }
    points.push({x: x, y: y});
  }
  return points;
}

function getPointsAsPath(points) {
  var d = "M 1 0 ";
  points.forEach(function(p, index, array) {
    d += "L " + p.x + " " + p.y + " ";
  });
  return d;
}

var points = getPoints(20);

var spiralCount = 0;
function spiral() {
  spiralCount++;
  var d = getPointsAsPath(points.slice(0, spiralCount));
  document.getElementById("path").setAttribute('d', d);
  if (spiralCount == points.length) {
    return;
  }
  setTimeout(spiral, 750);
}
setTimeout(spiral, 750);

function zoomIn() {
  timeout = timeIncrement;
  zoom *= 1.01;
  if (zoom > 10000000) {
    zoom = startZoom;
    timeout = initialPause;
  }
  if (count++ % 100 == 0) {
    console.log(zoom);
  }

  container.setAttribute('transform','scale(' + zoom + ')');
  container.setAttribute('stroke-width', 0.05 * startZoom / zoom); // constant width
  setTimeout(zoomIn, timeout);
}
setTimeout(zoomIn, initialPause);
